    private void Button4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Button4ActionPerformed
       double num7 = 0;
       double num8 = 0;
       double hypot;
       num7 = Double.parseDouble(Num7.getText());
       num8= Double.parseDouble(Num8.getText());
       hypot = Math.hypot(num7,num8);
       Ans4.setText(String.valueOf(hypot));
       
    }//GEN-LAST:event_Button4ActionPerformed

