    private void Button1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Button1ActionPerformed
       double num1 = 0;
       double num2 = 0;
       double large;
       num1 = Double.parseDouble(Num1.getText());
       num2= Double.parseDouble(Num2.getText());
       large = Math.max(num1,num2);
       Ans1.setText(String.valueOf(large));
       
    }//GEN-LAST:event_Button1ActionPerformed

    private void Button2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Button2ActionPerformed
       double num3 = 0;
       double num4 = 0;
       double small;
       num3 = Double.parseDouble(Num3.getText());
       num4= Double.parseDouble(Num4.getText());
       small = Math.min(num3,num4);
       Ans2.setText(String.valueOf(small));
       
    }//GEN-LAST:event_Button2ActionPerformed

